// DateRangePickerComponent.js
import React, { useState } from 'react';
import DateRangePicker from 'react-bootstrap-daterangepicker';
import 'moment/locale/en-au'; // Import locale for the date range picker
// import 'bootstrap/dist/css/bootstrap.css'; // Import Bootstrap CSS
import 'bootstrap/dist/css/bootstrap.css';
import 'react-datepicker/dist/react-datepicker.css';

function DateRangePickerComponent() {
  const [selectedDateRange, setSelectedDateRange] = useState({
    startDate: null,
    endDate: null,
  });

  const handleDateRangeChange = (event, picker) => {
    setSelectedDateRange({
      startDate: picker.startDate,
      endDate: picker.endDate,
    });
  };

  return (
    <div>
      <DateRangePicker onApply={handleDateRangeChange}>
        <button className="btn btn-primary">Select Date Range</button>
      </DateRangePicker>
      <p>
        Selected Date Range:{' '}
        {selectedDateRange.startDate &&
          selectedDateRange.startDate.format('LL')}{' '}
        -{' '}
        {selectedDateRange.endDate &&
          selectedDateRange.endDate.format('LL')}
      </p>
    </div>
  );
}

export default DateRangePickerComponent;
