import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.css';

function DateRangePickerComponent(data) {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [daterange, setDaterange] = useState(false);


//alert(BookingDates[0]);
  const onChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);
    if(end != null) {
        let diffDays = parseInt((end - start) / (1000 * 60 * 60 * 24))+1;
        alert(diffDays);
    setIsOpen(false);
    }
        
};

const handleClick = (e) => {
    e.preventDefault();
    if(isOpen == true) {
        setIsOpen(false);
        }else{
        setIsOpen(true);
        }
  };

  return (
    <div>
      <div className="mb-3">
      <button className="form-control text-center me-3"
       onClick={handleClick}
       >
        Chose dates
      </button>

      {isOpen && (
      <DatePicker
            selected={startDate}
            onChange={onChange}
            startDate={startDate}
            endDate={endDate}
            selectsRange
            excludeDates={data.data}
            selectsDisabledDaysInRange
            showDisabledMonthNavigation
            minDate={new Date()}
            inline
            className="form-control"

        />)}
        
      </div>
      <p>
        {startDate && startDate.toLocaleDateString("en-GB") }{ startDate && ' - '}
        {endDate && endDate.toLocaleDateString("en-GB")}
      </p>
      <div className="small mb-1" style={{color: '#F45757'}}> 3 Days</div>

    </div>
  );
}

export default DateRangePickerComponent;
