import React from 'react';
import './css/addProduct.css';
import { useEffect,useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.css';

const AddProudect = () => {
  const [User, setUser] = useState();
  const [user_name, setUser_name] = useState("");
  const [user_email, setUser_email] = useState("");
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [size, setSize] = useState("");
  const [price, setPrice] = useState("");
  const[color, setColor] = useState("");
  const[image, setImage] = useState("");

  const getUser = async () => {
    const token = localStorage.getItem("token") || "";
    let cus = {};

    try {
      const response = await axios.get(`http://localhost:8000/get-user-data`, {
        headers: {
          authorization: `Bearer ${token}`,
        },
      });

      cus = response.data;
      setUser_name(cus.user_name);
      setUser_email(cus.user_email);

      setUser(cus);
    } catch (error) {
      console.error(error);
    }
    return cus;
  };
  useEffect(() => {
    getUser();
  }, []);


  
  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default behavior of the event
  
    const data = {
      name: name,
      description: description,
      price: price,
      user_id: userIdapp,
      photo: image,
    };
  
    try {
      const response = await axios.post("http://localhost:5000/product", data);
  
      // Display success message
      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Product has been submitted successfully.',
      });
  
      console.log(response.data);
    } catch (error) {
      // Display error message
      Swal.fire({
        icon: 'error',
        title: 'Error!',
        text: 'Failed to submit the product.',
      });
  
      console.error(error.message);
    }
  };
    return (

<div className="container mt-2">
  <div className="row flex-lg-nowrap">
   
    <div className="col">
      <div className="row">
        <div className="col mb-3">
          <div className="card">
            <div className="card-body">
              <div className="e-profile">
                <div className="row">
