import React from 'react'
import { Link } from 'react-router-dom'
import "../pages/css/cat.css"
const nameToImageMap = {
  Wedding_Dress: './images/wedding-dress.jpg',
  Islamic_Dress: './images/wearing-hijab.jpg',
  Evening_dress: './images/evening-dress.jpg',
  // Other: 'path_to_image2.jpg',
  // Add more entries as needed
};

const CatCard=(data) =>{

  const imageUrl = nameToImageMap[data.name] ;


  return (
    <Link to= {`Shop/${data.id}`} className="product-link">
    <div className="col-md-3 col-sm-6 col-6" >
    <div className="product">
      <div className="product-card"> 
        <span className="sale">{data.name}</span> 
         <img src={imageUrl} className="rounded img-fluid" />
      </div>
    </div>
  </div>
  </Link>
  )
}

export default CatCard;
